-- ----------------------------
-- Table structure for t_news
-- ----------------------------
DROP TABLE IF EXISTS `t_news`;
CREATE TABLE `t_news`  (
  `newsId` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(40) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `publishDate` datetime(0) NULL DEFAULT NULL,
  `author` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `typeId` int(11) NULL DEFAULT NULL,
  `click` int(11) NULL DEFAULT NULL,
  `isHead` tinyint(4) NULL DEFAULT NULL,
  `isImage` tinyint(4) NULL DEFAULT NULL,
  `imageName` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `isHot` tinyint(4) NULL DEFAULT NULL,
  PRIMARY KEY (`newsId`) USING BTREE,
  INDEX `FK_t_news`(`typeId`) USING BTREE,
  CONSTRAINT `FK_t_news` FOREIGN KEY (`typeId`) REFERENCES `t_newstype` (`newsTypeId`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 146 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

